const fn char(code: u32) -> char {
    match char::from_u32(code) {
        Some(c) => c,
        None => unsafe { core::hint::unreachable_unchecked() },
    }
}

/// A [`Distribution`] for generating a random emoji.
pub struct Emoji;

impl Distribution<char> for Emoji {
    #[inline]
    fn sample<R: Rng + ?Sized>(&self, rng: &mut R) -> char {
        *EMOJIS.choose(rng).expect("`EMOJIS` is not empty")
    }
}
